int main() {
	ios::sync_with_stdio(false);
	cin.tie(nullptr);
	
	ll mod = 1e9 + 7;
	
	int n;
	cin >> n;
	
	auto get_ans = [&]() {
		--n;
	
		int block_index = n / block;
		int block_pos = n % block;
		
		int block_start = 2 * block_index;
		
		ll fpp3 = starts[block_start], fp2 = starts[block_start + 1];
		
		if (block_pos == 0) return fpp3;
		if (block_pos == 1) return fp2;
		
		for (int i = block * block_index + 2; i <= n; ++i) {
			ll f = 2 * fp2 + 3 * fpp3 + (i + 1);
			f %= mod;
			
			fpp3 = fp2;
			fp2 = f;
		}
		
		return fp2;
	};
	
	cout << get_ans() << "\n";
	
	return 0;
}
